import {useParams} from 'react-router-dom';

function CourseDetail(){
	// Let {course_id}=useParams();
	return(
		<div className="container mt-3"> 
			<div className="row"> 
				<div className="col-4"> 
					<img src="/logo512.png" className="card-img-top" alt="course image"/>
				</div>
					
				<div className="col-8"> 
					<h3>Course Title</h3>
					<p>Course Description </p>
					<p className="fw-bold"> Преподаватель: <a href="*">Teacher 1</a></p>
					<p className="fw-bold"> Длительность: 1ч 30мин</p>
					<p className="fw-bold"> Просмтотры: 453 студента</p>
					<p className="fw-bold"> Рейтинг:4/5</p>
				</div>
			{/*Видео-курсы*/}
				<div className="card mt-4">
					Видео курса
				  <ul className="list-group list-group-flush">
				    <li className="list-group-item">О чем этот курс <button className="btn btn-sm float-end"> <i class="bi-youtube"></i> </button></li>
				    <li className="list-group-item">Введение</li>
				    <li className="list-group-item">Глава 1</li>
				  </ul>
				</div>
			</div>
		</div>
	);
}

export default CourseDetail;
