import {Link} from 'react-router-dom';
function Sidebar() {
  return (
	<div className="card">
		<div className="list-group list-group-flush">
			<Link to='/user-personalcard' className="list-group-item list-group-item-action"> Личный кабинет</Link>
			<Link to='/user-my-courses' className="list-group-item list-group-item-action"> Курсы</Link>
			<Link to='/user-my-favoritecourses' className="list-group-item list-group-item-action"> Избранные курсы</Link>
			<Link to='/recommended-courses' className="list-group-item list-group-item-action"> Рекоммендованые курсы</Link>			<Link to='/profile-setting' className="list-group-item list-group-item-action"> Настройки профиля</Link>
			<Link to='/change-parrword' className="list-group-item list-group-item-action"> Сменить пароль</Link>
			<Link to='/logout' className="list-group-item list-group-item-action text-danger"> Выйти</Link>
		</div>
	</div>
  );
}

export default Sidebar;
