	const [teacherLoginData,setteacherLoginData]=useState({
		email:'',
		password:''
	});
	const handleChange=(event)=>{
		setteacherLoginData({
			...teacherLoginData, 
			[event.target.name]:event.target.value
		});
	}

	const submitForm=()=>{

	}

   useEffect(()=>{
    document.title='Вход | LMS ULSTU';
  });
  return (
			<div className="container mt-4 ">
        <div className="row">
          <aside className="col-md-3">
            <TeacherSidebar />
          </aside>
          <section className="col-md-9">
          	<div className="card">
		            <h5 className="card-header">Вход </h5>
		            <div className="card-body">
		              	<form>
								<div className="mb-3">
									<label for="exampleInputEmail1" className="form-label">Электронная почта</label>
									<input type="email" value={teacherLoginData.email} className="form-control" id="exampleInputEmail1" onChange={handleChange} aria-describedby="emailHelp"/>
								</div>
								<div className="mb-3">
									<label for="exampleInputPassword1" className="form-label">Пароль</label>
									<input type="password" value={teacherLoginData.password} className="form-control" onChange={handleChange} id="exampleInputPassword1"/>
								</div>
{/*								<div className="mb-3 form-check">
									<input type="checkbox" className="form-check-input" id="exampleCheck1"/>
									<label className="form-check-label" for="exampleCheck1">Запомнить меня</label>
								</div>*/}
								<button type="submit" onClick={submitForm} className="btn btn-primary">Войти</button>
								</form>
		            </div>
	        	</div>
          </section >
        </div>
      </div>		
  );
}

export default TeacherLogin;
